	padding-bottom: 5px;
	padding-left: 30px;
	align-items: left;
	width: 100%;
	height: 100%;
}
.textbox.row
{
	flex-direction: row;
}
.textbox.col
{
	flex-direction: column;
}

.text
{
	font-family: Arial;
	font-size: 36pt;
	color: #efefef;
}
.text.bold
{
	font-weight: bold;
}
.text.italic
{
	font-style: italic;
}
.text.center
{
	text-align: center;
}
.text.key
{
	color: white;
}
.text.gray
{
	color: Black;
}


"
