.body
{
	width: 1920px;
	height: 1080px;
	margin: 0;
	padding: 0;
}

.slide
{
	width: 100%;
	height: 100%;
	position: absolute;
	left: 0;
	top: 0;
	background-color: black;
}
.slide.key
{
	background-color: black;
}

.bar
{
	position: absolute;
	left: 0;
	bottom: 0;
	width: 100%;
	height: 20%;
	background-color: black; /*%color-main%;*/
	box-sizing: border-box;
}
.bar.key
{
	/*background: linear-gradient(to right, #dfdfdf, 5%, #dfdfdf, 20%, #dadada, 60%, #272727);*/
}

.textbox
{
	position: relative;
	display: flex;
	flex-direction: column;
	justify-content: stretch;
	box-sizing: border-box;
	padding-top: 20px;
	padding-bottom: 20px;
	padding-left: 50px;
	padding-right: 50px;
	align-items: center;
	width: 100%;
	height: 100%;
	box-sizing: border-box;!
}
.textbox.key
{
	background: radial-gradient(closest-side, rgba(170,170,170,1) 50%, rgba(0,0,0,1) 100%);
}

.textline
{
	box-sizing: border-box;
	position: relative;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	padding-top: 6px;
	padding-bottom:6px;
	padding-left: 100px;
	padding-right: 100px;
}


.text
{
	font-family: Trebuchet MS ;
	font-weight: bold;
	text-transform: uppercase;
	font-size: 48pt;
	color: #efefef;
	text-shadow: 6px 6px #272727;
	background-color: %color-main%;
	& p { margin: 0; }
}
.text.key
{
	box-sizing: border-box;
	color: white;
	text-shadow: 6px 6px #ffffff;
	background: linear-gradient(90deg, rgba(0,0,0,1) 0%, rgba(160,160,160,1) 25%, rgba(167,167,167,1) 50%, rgba(160,160,160,1) 75%, rgba(0,0,0,1) 100%);
}


"
